export const States = ({country,DarkMode}) => {

    return(
         <div className="states-div "> 

            <img className="states-flag" src={country.flags.svg} alt={country.name}/>

            {/* <div className={DarkMode ? "states-text dark-states-text":"states-text"}> */}
                <h2 className="state-name">{country.name}</h2>
            <span className="state-population"><span className="text-color">Population:</span> {country.population}</span>
            <span className="state-region"><span className="text-color">Region:</span>{country.region}</span>
            <span className="state-capital"><span className="text-color">Capital:</span>{country.capital}</span>
        </div>   
        // </div>

    )
}
