import Searchs from '../image/search-icons.png'
export const Search = ({setSearchCountry, DarkMode}) => {
const handleSearch = (e) =>{
    setSearchCountry(e.target.value)
};

 return(
    <div className={DarkMode ? "search-container dark-search-container": "search-container"}>
        <div className={DarkMode ?"search-icon-div dark-search-icon-div" :'search-icon-div'}>
            <img className="search-icon" src={Searchs} alt="search-icon" />
            </div>
        <input className={DarkMode ?"search-input dark-search-input":"search-input" } placeholder="search for a country..." onChange={handleSearch} />

    </div>
)
}
