export const States = ({country}) => {
    return(
        <div className="states-container">
            <div>
            <img className="states-flag" src={country.flags.svg} alt={country.name}/>
            <h2 className="state-name">{country.name}</h2>
            <span className="state-population">Population: {country.population}</span>
            <span className="state-region">Region:{country.region}</span>
            <span className="state-capital">Capital:{country.capital}</span>
            </div>

        </div>
    )
}
