 import {data} from '../data';
 import { useEffect, useState } from "react";
import Arrow from '../image/arrow.png'

 export const CountryPage=(description,DarkMode) =>{
    const {name} = useParams();
     const [Description,setDescrioption] = useState([])

     const [isLoading,setIsLoading] = useState(false);
  useEffect(() => {
    setIsLoading(true);
    setTimeout(()=>{
        setIsLoading(false);
    }, [1000]);
  }, []);
  if(isLoading){
    return(
   <div>Loading</div>

    )
  }

   const country = data.filter((country) => country.name===name)
 return(

   <div className={DarkMode ? "country-page-container dark-country-page-container" : "country-page-container "}>
    <link to={"/"}>
        {''}
    <button className="btn-back" onClick>
        <img src={Arrow} alt="arrow" />
        Back
    </button>
    </link>
     <div className={DarkMode ? "country-page dark-country-page":"country-page"}>
   {Description[0].name}
     <img src={Description[0].flag}/> 

    </div>

   </div>

 )
 }
