 import { useParams , Link} from "react-router-dom"
 import {data} from '../data';
 import { useEffect, useState } from "react";
import Arrow from '../image/arrow.png'


 export const CountryPage=({DarkMode, navigate}) =>{
    const {name} = useParams();
    const description = data.filter((country) => country.name===name)
    // const [Description,setDescription] = useState([]);


    // console.log(description[0].name);

     const [isLoading,setIsLoading] = useState(false);

  useEffect(() => {
    setIsLoading(true);
    setTimeout(()=>{
        setIsLoading(false);
    }, [1000]);
  }, []);
  if(isLoading){
    return(
   <div className="loading">Loading...</div>

    )
  }


 return(

   <div 
   className={DarkMode ? "country-page-container dark-country-page-container" : "country-page-container "}
   >
        {''}
        <Link  to={"/"} className={DarkMode ?"back-link dark-back-link":"back-link"}>
    <button className="btn-back" >
        <img className="arrow" src={Arrow} alt="arrow" />
        Back
    </button>
    </Link>
     <div 
     className={DarkMode ? "country-page dark-country-page":"country-page"}
     >
   {/* {description[0].name} */}
     <img className="individual-flag" src={description[0].flag}/> 


    <div className={DarkMode ?"country-description dark-country-description":"country-description" }>  
        <h3 className={DarkMode ? "cd dark-cd" : "cd" }>{description[0].name}</h3>
        <div className={DarkMode ? "cd dark-cd" : "cd" }> Native Name:<span className="descript-color">{description[0].nativeName}</span></div>
        <div className={DarkMode ? "cd dark-cd" : "cd" }> Population: <span className="descript-color">{description[0].population}</span></div>
        <div className={DarkMode ? "cd dark-cd" : "cd" }>Region:<span className="descript-color">{description[0].region}</span></div>
        <div className={DarkMode ? "cd dark-cd" : "cd" }>Sub Region:<span className="descript-color">{description[0].subregion}</span></div>
        <div className={DarkMode ? "cd dark-cd" : "cd" }>Capital:<span className="descript-color">{description[0].capital}</span></div>
        <div className={DarkMode ? "cd dark-cd" : "cd" }>Tol Level Domain:<span className="descript-color">{description[0].topLevelDomain}</span></div>
        <div className={DarkMode ? "cd dark-cd" : "cd" }>Currencies:<span className="descript-color">{description[0].currencies[0].name}</span></div>
        <div className={DarkMode ? "cd dark-cd" : "cd" }>Languages:<span className="descript-color">{description[0].languages[0].name}</span></div>

            <div className="border-country">
              Border Countries:
              {description[0]?.borders && description[0].borders.map((border) => {
                return (
                  <button onClick={() => navigate(`/${border}`)} className="descript-color  border">{borders} </button>
                )
              })}

              </div> 
   </div> 

    </div> 

   </div>

 )
 }
