export const Filter =  ({DarkMode,setSelect}) => {

    function handleSelect(e){
    setSelect(e.target.value)
    }
return(
    <div className={DarkMode?"filter-div dark-filter-div":"filter-div"} >

        <select className="filter-select" name="filter-container" id="filter" onChange={handleSelect}>

            <option  className="filter-option" value="">Filter by Region</option>
            <option value="">All</option>
            <option value="">Africa</option>
            <option value="">America</option>
            <option value="">Asia</option>
            <option value="">Oceania</option>
            <option value="">Europe</option>

        </select>
    </div>
)
}
