 import { useParams } from "react-router-dom"
 import {data} from '../data';
 import { useEffect, useState } from "react";
import Arrow from '../image/arrow.png'


 export const CountryPage=() =>{
    const {name} = useParams();
    const description = data.filter((country) => country.name===name)
    // const [Description,setDescription] = useState([]);

    console.log(description[0].name);

     const [isLoading,setIsLoading] = useState(false);

  useEffect(() => {
    setIsLoading(true);
    setTimeout(()=>{
        setIsLoading(false);
    }, [1000]);
  }, []);
  if(isLoading){
    return(
   <div>Loading</div>

    )
  }


 return(

   <div 
  //  className={DarkMode ? "country-page-container dark-country-page-container" : "country-page-container "}
   >
    {/* <link to={"/"}> */}
        {''}
    <button className="btn-back" >
        <img src={Arrow} alt="arrow" />
        Back
    </button>
    {/* </link> */}
     <div 
    //  className={DarkMode ? "country-page dark-country-page":"country-page"}
     >
   {description[0].name}
     <img src={description[0].flag}/> 

    </div>
    <div className="country-description"> 
        <h3>{description[0].name}</h3>
        <div> Native Name:{description[0].nativeName}</div>
        <div> Population: {description[0].population}</div>
        <div>Region:{description[0].region}</div>
        <div>Sub Region:{description[0].subregion}</div>
        <div>Capital:{description[0].capital}</div>
        <div>Tol Level Domain:{description[0].topLevelDomain}</div>
        <div>Currencies:{description[0].currencies}</div>
        <div>Languages:{description[0].languages}</div>
        <div>Border Countries:{description[0].borders}</div>
    </div> 

   </div>

 )
 }
