 import { useParams , Link} from "react-router-dom"
 import {data} from '../data';
 import { useEffect, useState } from "react";
import Arrow from '../image/arrow.png'


 export const CountryPage=({DarkMode}) =>{
    const {name} = useParams();
    const description = data.filter((country) => country.name===name)
    // const [Description,setDescription] = useState([]);


    // console.log(description[0].name);

     const [isLoading,setIsLoading] = useState(false);

  useEffect(() => {
    setIsLoading(true);
    setTimeout(()=>{
        setIsLoading(false);
    }, [1000]);
  }, []);
  if(isLoading){
    return(
   <div>Loading</div>

    )
  }


 return(

   <div 
   className={DarkMode ? "country-page-container dark-country-page-container" : "country-page-container "}
   >
        {''}
        <Link  to={"/"} className={DarkMode ?"back-link dark-back-link":"back-link"}>
    <button className="btn-back" >
        <img className="arrow" src={Arrow} alt="arrow" />
        Back
    </button>
    </Link>
     <div 
     className={DarkMode ? "country-page dark-country-page":"country-page"}
     >
   {/* {description[0].name} */}
     <img className="individual-flag" src={description[0].flag}/> 


    <div className={DarkMode ?"country-description dark-country-description":"country-description" }>  
        <h3>{description[0].name}</h3>
        <div> Native Name:<span className="descript-color">{description[0].nativeName}</span></div>
        <div> Population: {description[0].population}</div>
        <div>Region:{description[0].region}</div>
        <div>Sub Region:{description[0].subregion}</div>
        <div>Capital:{description[0].capital}</div>
        <div>Tol Level Domain:{description[0].topLevelDomain}</div>
        <div>Currencies:{description[0].currencies[0].name}</div>
        <div>Languages:{description[0].languages[0].name}</div>
        {description[0]?.borders &&   <div className="border-country">Border Countries:{description[0].borders}</div> }
   </div> 
    </div> 

   </div>

 )
 }
