import Sun from '../image/light-mode.png'
 import Moon from '../image/moon.jpeg'
import { useState } from 'react'
export const Header = () => {
   const [DarkMode, setDarkMode] = useState(false);
   function getDarkMode (){
    setDarkMode(!DarkMode)
   }
    return(
        <div className={DarkMode ? "header bg-dark header-text-dark" : "header  "}>
            <div className='header-text header-text-dark'>

                <h3 >Where is the world?</h3>
            </div>
            <div className='header-left-aside' onClick={getDarkMode}>
            <img className="sun" src={DarkMode ? Moon : Sun} alt="icon-sun" /> 
                {DarkMode ? "Dark Mode": "Light Mode"}


                <span className='mode'></span>
            </div>
        </div>
    )
}
